import React from "react";
import { StyleSheet, TouchableOpacity, Image, Text, View } from "react-native";
import { useNavigation } from '@react-navigation/native';
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import tokenTypeContext from "../../context/tokenType";


const HeaderMenu = (props) => {

  const navigation = useNavigation();

  const {token,setToken,type, setType} = React.useContext(tokenTypeContext);

  const Icons = () => {
    console.log(token)
    console.log(type)
    if(type == 0 || type == 1 || type == 2){
        return (
            <TouchableOpacity onPress={() => navigation.toggleDrawer()}>
                <MaterialCommunityIcons name="hamburger" size={50} color="black" />
            </TouchableOpacity>
        )
    }else{
        return (
            <TouchableOpacity onPress={() => navigation.goBack()}>
                <Image
                    source={require("../../images/back_icon.png")}
                    style={styles.ImageBackStyle}
                />
            </TouchableOpacity>
        )
    }
}

  return (
    <View>
      <View style={styles.statusBar}></View>
      <View style={styles.header}>
        <Icons/>
        <Text style={styles.heading}>{props.content}</Text>
        
        <View style={{height: 50,flex: 1,width: 50}}></View></View>
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    backgroundColor: "#f1cbae",
    height: 70,
    flexDirection: "row",
    lineHeight: 0,
    justifyContent: "flex-start",
  },
  statusBar: {
    height: 25,
    backgroundColor: "#5c3b28",
  },
  ImageBackStyle: {
    height: 20,
    flex: 1,
    width: 40,
    resizeMode: "contain",
    marginLeft:"10%",
  },
  heading: {
    alignSelf:"center",
    justifyContent: "center",
    flex:3,
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
    color: "#5C3B28",
  },
});

export default HeaderMenu;
